# 内存和硬盘参数的补全
def completion_ram(ram):
    # 把ram转化成字符串类型
    ram = str(ram)
    # 如果ram中包含'其他' 或者 包含'GB'，就直接return
    if '其他' in ram or 'GB' in ram:
        return ram;
    # 或者如果只包含G没有包含B，就加B后return
    elif 'G' in ram:
        return ram + 'B'
    # 或者没有包含GB也没有包含B，因此只有数字，就加GB后return
    else:
        return ram + 'GB'


def match_ram_rom(s):
    #s = str(s).replace('4G+全网通','').replace('4G全网通','').replace('全网通4G','')
    s = str(s)[str(s).index(' '):]
    return_data = {
        'ram' : '其他',
        'rom' : '其他',
    }
    if re.search('[0-9]*G?B?\+?[0-9]+GB?', s):
        # 获得匹配的字符串
        result = re.search('[0-9]*G?B?\+?[0-9]+GB?', s)[0]
        # print(result)
        # 如果匹配的ram + rom中带有 + 号
        if '+' in result:
            # 按加号进行分割，并把第一个元素赋给ram
            return_data['ram'] = str(result).split('+')[0]
            # 按加号进行分割，并把第二个元素赋给rom
            return_data['rom'] = str(result).split('+')[1]
        else:
            return_data['rom'] = str(result)
        return_data['rom'] = completion_ram(return_data['rom'])
        return_data['ram'] = completion_ram(return_data['ram'])
    return return_data

def remove_name(s):
    for y in word_need_to_remove:
        s = s.replace(y, '')
    s = s.replace('Redmi', '红米')
    return s

def parse_brand_and_model(result_array):
    brand_and_model = {}
    if '/' in result_array[0]:
        brand = result_array[0].split('/')[1]
        if brand == '苹果':
            brand = 'apple'
        elif brand == '荣耀':
            brand = '华为'
        #elif brand in ''.join(result_array[1:]).lower():
        #    brand = ''
    else:
        brand = result_array[0].lower()
    # print(brand)
    if brand == '三星' and re.search('[0-9]?G?\+?[0-9]+GB?', result_array[-1]):
        model = brand + ''.join(result_array[1:-2]).lower()
    elif brand == '三星' or re.search('[0-9]?G?\+?[0-9]+GB?', result_array[-1]):
        model = brand + ''.join(result_array[1:-1]).lower()
    elif brand == '守护宝':
        model = brand + ''.join(result_array[1:]).lower().replace('上海中兴', '')
    else:
        model = brand + ''.join(result_array[1:]).lower()
    brand_and_model['brand'] = brand
    brand_and_model['model'] = model
    return brand_and_model

