def parse_as_phone(selector):  #参数是x 和 selector
    phone = Phone()
    # _id id name brand model url 外面赋值
    # cpu resolution screen_size r_camera hot_spot 表格里
    phone.cpu = '其他'
    phone.resolution = phone.screen_size = phone.r_camera = phone.hot_spot = '暂无信息'
    ths = [str(z).strip() for z in selector.xpath(
        '//table[@class="tm-tableAttr"][1]/tbody/tr[not(@class="tm-tableAttrSub")]/th/text()').extract()]
    # print(ths)
    tds = [str(z).replace('\xa0', ' ').strip() for z in selector.xpath(
        '//table[@class="tm-tableAttr"][1]/tbody/tr[not(@class="tm-tableAttrSub")]/td/text()').extract()]
    # print(tds)
    if '品牌' in ths:
        phone.brand = tds[ths.index('品牌')]
    if '型号' in ths:
        phone.model = tds[ths.index('型号')]
    if 'CPU型号' in ths:
        phone.cpu = tds[ths.index('CPU型号')]
    if 'CPU型号' not in ths and 'CPU品牌' in ths:
        phone.cpu = tds[ths.index('CPU品牌')]
    if '分辨率' in ths:
        phone.resolution = tds[ths.index('分辨率')]
    if '屏幕尺寸' in ths:
        phone.screen_size = tds[ths.index('屏幕尺寸')]
    if '后置摄像头' in ths:
        phone.r_camera = tds[ths.index('后置摄像头')]
    if '手机类型' in ths:
        phone.hot_spot = str(tds[ths.index('手机类型')]).split(' ')
    if '运行内存RAM' in ths and phone.ram.find('GB') == -1:
        phone.ram = tds[ths.index('运行内存RAM')]
    if phone.ram.find('GB') == -1:
        phone.ram = '暂无信息'
    # * packlist
    ths = selector.xpath(
        '//table[@class="tm-tableAttr"][2]/tbody/tr[not(@class="tm-tableAttrSub")]/th/text()').extract()
    # print(ths)
    tds = selector.xpath(
        '//table[@class="tm-tableAttr"][2]/tbody/tr[not(@class="tm-tableAttrSub")]/td/text()').extract()
    # print(tds)
    for z in range(0, len(ths)):
        phone.pack_list = phone.pack_list + ths[z] + 'x' + tds[z] + ','
    if len(ths) == 0:
        phone.pack_list = '暂无信息'

    video_url = selector.xpath('.').re(
        'imgVedioUrl\":\"//cloud.video.taobao.com/play/.*/(.*?).swf')
    if video_url == []:
        phone.video_url = '暂无信息'
    else:
        phone.video_url = 'http://cloud.video.taobao.com/play/u/p/1/e/6/t/1/' + video_url[
            0] + '.mp4'

    phone.thumb_pic = [str(z).replace('60x60', '430x430') for z in
                       selector.xpath('//ul[@id="J_UlThumb"]/li/a/img/@src').extract()]

    desc = selector.xpath('.').re('descUrl\":\"(.*?)\"')
    if desc != []:
        desc_response = requests.get('http:' + str(desc[0]), headers=desc_headers)
        desc_selector = Selector(text=desc_response.text)
        desc_url = desc_selector.xpath('.').re(
            '<img align=\"absmiddle\" src=\"(.*?)\"') + desc_selector.xpath('.').re('<img src=\"(.*?)\"')
    phone.detail_url = desc_url

    # * tmail 不存在的 memory_card max_mem_sup figer battery f_camera
    phone.memory_card = phone.max_mem_sup = phone.figer = phone.battery = phone.f_camera = '暂无信息'
    #print(phone.__dict__)
    return phone


